package ru.cpsmi.spring.model;import java.util.ArrayList;import java.util.List;import java.util.Objects;import java.util.stream.Stream;import static java.util.stream.Collectors.toList;public class PersonPageTO {    private String number;    private int totalPersons;    private List<SimplePerson> persons = new ArrayList<>();    public String getNumber() {        return number;    }    public int getTotalPersons() {        return totalPersons;    }    public List<Integer> getPages() {        return Stream.iterate(1, i -> i + 1).limit((totalPersons - totalPersons%10)/10 + 1 ).collect(toList());    }    public List<SimplePerson> getPersons() {        return persons;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        PersonPageTO that = (PersonPageTO) o;        return totalPersons == that.totalPersons &&                Objects.equals(number, that.number);    }    @Override    public int hashCode() {        return Objects.hash(number, totalPersons);    }    @Override    public String toString() {        return "PersonPageTO{" +                "number='" + number + '\'' +                ", persons=" + persons.size() +                ", totalPersons=" + totalPersons +                '}';    }    public void setNumber(String number) {        this.number = number;    }    public void setTotalPersons(int totalPersons) {        this.totalPersons = totalPersons;    }    public void setPersons(List<SimplePerson> persons) {        this.persons = persons;    }    public void addPerson(SimplePerson person) {        getPersons().add(person);    }}