package ru.cpsmi.spring.model;import java.util.Objects;public class SimplePerson {    private String id;    private String name;    public String getId() {        return id;    }    public String getName() {        return name;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        SimplePerson that = (SimplePerson) o;        return Objects.equals(id, that.id) &&                Objects.equals(name, that.name);    }    @Override    public int hashCode() {        return Objects.hash(id, name);    }    @Override    public String toString() {        return "SimplePerson{" +                "id='" + id + '\'' +                ", name='" + name + '\'' +                '}';    }    public void setId(String id) {        this.id = id;    }    public void setName(String name) {        this.name = name;    }}